import React, { useEffect, useState, useContext } from "react";
import { Container, Row, Col, Table, Spinner, Modal } from "react-bootstrap";
import SideBar from "../SideBar/SideBar";
import "./DashBoard.css";
import { DataContext } from "../Data";
import Hamburger from "../Hamburger/Hamburger";
import { Helmet } from "react-helmet";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {UserContext} from "../Context/Sign_In_Context";
import ImagePicker from 'react-image-picker'
import 'react-image-picker/dist/index.css'

import img1 from '../../images/emotions/1.png'
import img2 from '../../images/emotions/2.png'
import img3 from '../../images/emotions/3.png'
import img4 from '../../images/emotions/4.png'
import img5 from '../../images/emotions/5.png'

const imageList = [img1, img2, img3, img4, img5]

const DashBoard = () => {
  const [user, setUser] = useContext(UserContext);
  const [data, setData] = useContext(DataContext);
  const [startDate, setStartDate] = useState(new Date());
  const [parentMoodIndex, setParentMoodIndex] = useState("");
  const [childMoodIndex, setChildMoodIndex] = useState("");

  const [childsMood, setChildsMood] = useState({});
  const [parentsMood, setParentsMood] = useState({});

  const [image,setImage] = useState(null);

  const onPick = (image) => {
      setImage({image})
  }


  const handleSubmitButtonCLick = (event) => {
    let data = {
      "childsMood": {
              "year": startDate.getUTCFullYear(),
              "month": startDate.getMonth(),
              "day":startDate.getDate(),
              "score": parseInt(childMoodIndex)
          },
      "parentsMood": {
              "year": startDate.getUTCFullYear(),
              "month":startDate.getMonth(),
              "day":startDate.getDate(),
              "score": parseInt(parentMoodIndex)
          }
    }

    fetch("http://localhost:5000/Appointment/" +user.selectedUser._id , {
      method: "PUT",
      body: JSON.stringify(data),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
        .then((response) => response.json())
        .then((data) => {
          console.log("SUCCESS!!")
        });
  }

  return (
    <Container fluid className="dashboard">
      <Helmet>
        <title>Dashboard | Doctors Portal</title>
        <meta name="description" content="Now a days All things are based on Online. Find a good doctor is so  much hassle  now a days.
        Doctors portal is going to give you advantage that you don't need to go hospital physically.You can book and get the best treatment from now ."/>
        <meta name="keywords" content="online doctors book appointment"/>
      </Helmet>
      <div className="ml-6 main">
        <div className="d-md-block">
          <SideBar></SideBar>
        </div>
        <div className="title__hamburger">
          <h3 className="mt-3">Hello {user.selectedUser.firstname}'s parent</h3>
          <Hamburger></Hamburger>
        </div>



        <div>
          {/*<form>*/}
          <text>Pick the date</text>
          <br/>
          <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
          <br/>
          <br/>
          <text>How are you feeling today?</text>
          <br/>
          <input
              type='text'
              name='parentMoodIndex'
              value={parentMoodIndex}
              onChange={e => setParentMoodIndex(e.target.value)}
          />
          <br/>
          <br/>

          <text>How is your kid feeling today?</text>
          <br/>
          <input
              type='text'
              name='childMoodIndex'
              value={childMoodIndex}
              onChange={e => setChildMoodIndex(e.target.value)}
          />
          <br/>
          <br/>

          <text>How is your you [PARENT_NAME] feeling today?</text>
          <ImagePicker
                images={imageList.map((image, i) => ({src: image, value: i}))}
                onPick={this.onPick}
          />
          <button type="button" onClick={() => console.log(this.state.image)}>OK</button>


          <br/>
          <br/>


          <button onClick={e=> handleSubmitButtonCLick(e)}>Submit</button>
          {/*</form>*/}
        </div>
      </div>

    </Container>
  );
};

export default DashBoard;
