import React, { useEffect, useState, useContext } from "react";
import SideBar from "../SideBar/SideBar";
import { Container, Row, Col, Table, Spinner } from "react-bootstrap";
import { Link, useLocation, BrowserRouter as Router } from "react-router-dom";
import { DataContext } from "../Data";
import Hamburger from "../Hamburger/Hamburger";
import { Helmet } from "react-helmet";

import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

const condition = Boolean(Math.round(Math.random()))

const ChildsMoodsOptions = {
    chart: {
        type: 'spline'
    },
    rangeSelector: {
        selected: 1
    },
    title: {
        text: "Child's Mood"
    },
    series: [
        {
            data: [[Date.UTC(2021, 1, 1),1],
                    [Date.UTC(2021, 1, 2),2],
                    [Date.UTC(2021, 1, 3),2],
                    [Date.UTC(2021, 1, 4),1],
                    [Date.UTC(2021, 1, 5),4],
                    [Date.UTC(2021, 1, 6),6]]
        }
    ],
    xAxis: {
        type: 'datetime',
        dateTimeLabelFormats: {
            day: condition ? "%e-%b-%y" : "%e\%b\%y",
            month: condition ? "%b-%y" : '%b \'%y'
        }
    }
};

const ParentsMoodsOptions = {
    chart: {
        type: 'spline'
    },
    rangeSelector: {
        selected: 1
    },
    title: {
        text: "Parents's Mood"
    },
    series: [
        {
            data: [[Date.UTC(2021, 1, 1),1],
                [Date.UTC(2021, 1, 2),2],
                [Date.UTC(2021, 1, 3),2],
                [Date.UTC(2021, 1, 4),1],
                [Date.UTC(2021, 1, 5),4],
                [Date.UTC(2021, 1, 6),6]]
        }
    ],
    xAxis: {
        type: 'datetime',
        dateTimeLabelFormats: {
            day: condition ? "%e-%b-%y" : "%e\%b\%y",
            month: condition ? "%b-%y" : '%b \'%y'
        }
    }
};

const options3 = {
    chart: {
        type: 'spline'
    },
    title: {
        text: 'My chart'
    },
    series: [
        {
            data: [3, 5, 6, 2, 4, 2]
        }
    ]
};

const KidsDetails = (props) => {
    const [data, setData] = useContext(DataContext);
    const [selectedKid, setSelectedKid] = useState([])
    const [user, setUser] = useState([]);
    const [userInfo, setUserInfo] = useState([]);
    let kid_index = useLocation().pathname.split("/").pop()
    console.log(kid_index)


    useEffect(() => {
        fetch("http://localhost:5000/Appointment/"+kid_index)
            .then((response) => response.json())
            .then((jsun) => setSelectedKid(jsun.reverse()));
    }, []);
    console.log("Selected Kid:")
    console.log(selectedKid);


    const ChildsMoodsOptions = {
        chart: {
            type: 'spline'
        },
        rangeSelector: {
            selected: 1
        },
        title: {
            text: "Child's Mood"
        },
        series: [
            {
                data: [[Date.UTC(2021, 1, 1),1],
                    [Date.UTC(2021, 1, 2),2],
                    [Date.UTC(2021, 1, 3),2],
                    [Date.UTC(2021, 1, 4),1],
                    [Date.UTC(2021, 1, 5),4],
                    [Date.UTC(2021, 1, 6),6]]
            }
        ],
        xAxis: {
            type: 'datetime',
            dateTimeLabelFormats: {
                day: condition ? "%e-%b-%y" : "%e\%b\%y",
                month: condition ? "%b-%y" : '%b \'%y'
            }
        }
    };

    const ParentsMoodsOptions = {
        chart: {
            type: 'spline'
        },
        rangeSelector: {
            selected: 1
        },
        title: {
            text: "Parents's Mood"
        },
        series: [
            {
                data: [[Date.UTC(2021, 1, 1),1],
                    [Date.UTC(2021, 1, 2),2],
                    [Date.UTC(2021, 1, 3),2],
                    [Date.UTC(2021, 1, 4),1],
                    [Date.UTC(2021, 1, 5),4],
                    [Date.UTC(2021, 1, 6),6]]
            }
        ],
        xAxis: {
            type: 'datetime',
            dateTimeLabelFormats: {
                day: condition ? "%e-%b-%y" : "%e\%b\%y",
                month: condition ? "%b-%y" : '%b \'%y'
            }
        }
    };


    useEffect(() => {
        if (data.length !== 0) {
            const uniq = [...new Set(data.map((x) => x.firstname))];
            setUser(uniq);
        }
    }, [data]);
    useEffect(() => {
        if (user.length !== 0) {
            const fake = user.map((x) => {
                const te = data.find((y) => y.firstname === x);
                return te;
            });
            setUserInfo(fake);
        }
    }, [user, data]);

    return (
        <Container fluid>
            <Helmet>
                <title>Patients List | Doctors Portal</title>
                <meta name="description" content="Now a days All things are based on Online. Find a good doctor is so  much hassle  now a days.
        Doctors portal is going to give you advantage that you don't need to go hospital physically.You can book and get the best treatment from now ."/>
                <meta name="keywords" content="online doctors book appointment"/>
            </Helmet>
            <div className="main ml-6">
                <div className="d-md-block">
                    <SideBar></SideBar>
                </div>
                <div className="title__hamburger">
                    <h3 className="mt-3">Kids List</h3>
                    <Hamburger></Hamburger>
                </div>

                <div>
                    <HighchartsReact highcharts={Highcharts} options={ChildsMoodsOptions} />
                    <HighchartsReact highcharts={Highcharts} options={ParentsMoodsOptions} />
                    <HighchartsReact highcharts={Highcharts} options={options3} />
                </div>

            </div>
        </Container>
    );
};

export default KidsDetails;
